mapper.select:
select * from jp_platform where fileType = #{platform.fileType,jdbcType=INTEGER}
and versionName like '%${platform.versionName}%'
order by versionNo desc

mapper.update:
update jp_platform set versionName = #{platform.versionName},versionNo
= #{platform.versionNo},
minUpdateVersion =
#{platform.minUpdateVersion},upgradeDes = #{platform.upgradeDes}

	,fileRealName = #{platform.fileRealName}

,downloadUrl = #{platform.downloadUrl}
where id = #{platform.id}

update jp_platform
set isUsed = 0
where fileType = #{fileType,jdbcType=INTEGER}
