update.foreach:
update  jp_dynamic 
set deleteflag = 1
where dyid = #{item,jdbcType=VARCHAR}

mapper.select:
SELECT dyread.readNum, dy.dyid, dy.dytype, dy. branchid, dy. dytitle, dy. deleteflag, dy. imgurl, dy. branchname, dy. createid, dy. createtime, dy. updateid, dy. updatetime, dy. createname, dy. type, dy.dycontent FROM jp_dynamic dy LEFT JOIN ( SELECT count(id) AS readNum, dyread.dyid FROM jp_dyread dyread
GROUP BY dyread.dyid ) dyread ON dyread.dyid = dy.dyid where 1 = 1 and dy.deleteflag = 0
and dy.branchid = #{dynamic.branchid,jdbcType=VARCHAR}
and dy.type = #{dynamic.type,jdbcType=VARCHAR}
and dy.branchid in
#{item}

SELECT dyread.readNum, dy.dyid, dy.dytype, dy. branchid, dy. dytitle, dy. deleteflag, dy. imgurl, dy. branchname, dy. createid, dy. createtime, dy. updateid, dy. updatetime, dy. createname, dy. type, dy.dycontent FROM jp_dynamic dy LEFT JOIN ( SELECT count(id) AS readNum, dyread.dyid FROM jp_dyread dyread
GROUP BY dyread.dyid ) dyread ON dyread.dyid = dy.dyid where 1 = 1 and dy.deleteflag = 0
and dy.familyid = #{dynamic.familyid,jdbcType=VARCHAR}
and dy.branchid = #{dynamic.branchid,jdbcType=VARCHAR}
and dy.type = #{dynamic.type,jdbcType=VARCHAR}

select t.* from (
	select m.* from jp_dynamic m left join jp_branch n on m.branchid = n.branchid 
	where n.areacode = #{cityCode} and n.familyid = #{familyid}
	union 
	select m.* from jp_dynamic m left join jp_branch n on m.branchid = n.branchid 
	where n.citycode = #{cityCode} and n.familyid = #{familyid}
	union 
	select m.* from jp_dynamic m left join jp_branch n on m.branchid = n.branchid 
	where n.xcode = #{cityCode} and n.familyid = #{familyid}
	) t 
	order by t.createtime desc
	
    limit #{startRow} , #{pageSize}

  select * from jp_dynamic 
  where 
  (dytitle like  CONCAT("%",#{content,jdbcType=VARCHAR},"%") 
  	or 
  dycontent like  CONCAT("%",#{content,jdbcType=VARCHAR},"%")) 
  and familyid=#{familyid} 
  and deleteflag = 0
  
  	and branchid in 
  		
  			#{item}
        
  
   order by updatetime desc 
    
			limit ${count}
		
		
			offset ${start}

